rule.definition:
=#"SYSTEM_SYSRULES_gridRowLayout"(
  contents: {
    #"SYSTEM_SYSRULES_imageField"(
      label: ri!OrderItems[ri!index].Product.name,
      images: {
        #"SYSTEM_SYSRULES_documentImage"(
          document: todocument(if(isnull(ri!OrderItems[ri!index].Product.productImageDocId), #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3836", ri!OrderItems[ri!index].Product.productImageDocId))	
        )
      },
      size: "THUMBNAIL"
    ),
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      label:"Product",
      value: {
        #"SYSTEM_SYSRULES_richTextItem"(
          text: #"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3580"(ri!OrderItems[ri!index].Product.name,100),
          style: "STRONG"
        ),
        #"SYSTEM_SYSRULES_richTextItem"(text: char(10) & ri!OrderItems[ri!index].Product.productCategory),
        #"SYSTEM_SYSRULES_richTextItem"(text: char(10) & "SKU: ", style: "STRONG"),
        #"SYSTEM_SYSRULES_richTextItem"(text: ri!OrderItems[ri!index].Product.sku)
      },
      readOnly:true
    ),
    #"SYSTEM_SYSRULES_textField"(
      label: "Unit Price",
      value: if(
        isnull(ri!OrderItems[ri!index].OrderItemUnitPrice),
        #"_a-0000dc96-9a5e-8000-6a17-01ef9001ef90_4707"(ri!OrderItems[ri!index].Product.price),
        #"_a-0000dc96-9a5e-8000-6a17-01ef9001ef90_4707"(ri!OrderItems[ri!index].OrderItemUnitPrice)
      ),
      readOnly: true
    ),
    #"SYSTEM_SYSRULES_integerField"(
      label: "Quantity",
      value: ri!OrderItems[ri!index].Quantity,
      saveInto: ri!OrderItems[ri!index].Quantity,
      refreshAfter:"KEYPRESS",
      required: true(),
      requiredMessage: "A quantity must be specified",
      readOnly: if(not(ri!readOnly),#"_a-0000d9a9-eb56-8000-9e06-010000010000_751"(ri!OrderItems[ri!index].Product.name),ri!readOnly),
      validations: if(ri!readOnly, null, if(ri!OrderItems[ri!index].Quantity > ri!OrderItems[ri!index].Product.quantity, "Not enough stock to  fulfill order.  Only " & ri!OrderItems[ri!index].Product.quantity & " items are available.", null))
    ),
    #"SYSTEM_SYSRULES_imageField"(
      label:"delete " & ri!index,
      images: #"SYSTEM_SYSRULES_documentImage"(
        document: #"SYSTEM_SYSRULES_iconIndicator"(
          if(
            ri!readOnly,
            "REMOVE_DISABLED",
            "REMOVE"
          )
        ),
        altText: "Delete Item",
        caption: "Delete Item",
        link: if(
          ri!readOnly,
          null,
          #"SYSTEM_SYSRULES_dynamicLink"(
            value: ri!index,
            saveInto: {
              a!save(ri!OrderItems, remove(ri!OrderItems, save!value)),
              a!save(ri!itemsToken, remove(ri!itemsToken, save!value))
            }
          )
        )
      ),
      size:"ICON"
    )
  },
  id: ri!index
)
