rule.definition:
load(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 10,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "manufacturer",
      ascending: true
    )
  ),
  local!gridPagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 5,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "manufacturer",
      ascending: false()
    )
  ),
  local!selectedManufacturer,
  with(
    local!datasubset: queryrecord(
      #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3830",
      #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "manufacturer", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "id", aggregationFunction: "COUNT"),
        }),
        pagingInfo: local!pagingInfo
      )
    ),
    local!gridDatasubset: if(
      isnull(local!selectedManufacturer),
      null,
      queryrecord(
      #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3830",
      #"SYSTEM_SYSRULES_query"(
        selection: #"SYSTEM_SYSRULES_querySelection"(
          columns: {
          #"SYSTEM_SYSRULES_queryColumn"(field: "name"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "id"),
          #"SYSTEM_SYSRULES_queryColumn"(field:"productImageDocId")
        }),
        filter:#"SYSTEM_SYSRULES_queryFilter"(field:"manufacturer",operator:"=",value:local!selectedManufacturer),
        pagingInfo: local!gridPagingInfo
      )
    )),
    if(
      isnull(local!selectedManufacturer),
      #"SYSTEM_SYSRULES_gridField"(
        label:"Number of Products per Manufacturer",
        totalCount:local!datasubset.totalCount,
        columns:{
          #"SYSTEM_SYSRULES_gridTextColumn"(
            label:"Name",
            data:local!datasubset.data.manufacturer,
            field:"manufacturer",
            links:apply(
              #"SYSTEM_SYSRULES_dynamicLink"(value:_,saveInto:local!selectedManufacturer),
              local!datasubset.data.manufacturer
            )
          ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
            label:"Total",
            alignment:"RIGHT",
            data:local!datasubset.data.id,
            field:"id"
          )
        },
        value:local!pagingInfo,
        saveInto:local!pagingInfo
      ),
      {
        #"SYSTEM_SYSRULES_ButtonLayout"(
          secondaryButtons: #"SYSTEM_SYSRULES_ButtonWidget"(
            label: "Go back",
            value: null,
            saveInto: {
              local!selectedManufacturer,
              a!save(local!gridPagingInfo.startIndex, 1)
            }
          )
        ),
        #"SYSTEM_SYSRULES_gridField"(
          label: "List of Products for "&local!selectedManufacturer,
          totalCount: local!gridDatasubset.totalCount,
          columns: {
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label:"Name",
              data:local!gridDatasubset.data.name,
              field:"manufacturer",
              links:apply(
                #"SYSTEM_SYSRULES_recordLink"(recordType:#"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3830",identifier:_),
                local!gridDatasubset.identifiers
              )
            ),
            #"SYSTEM_SYSRULES_gridImageColumn"(
              label:"Image",
              data:apply(
                #"SYSTEM_SYSRULES_documentImage"(
                  document:_
                ),
                todocument(local!gridDatasubset.data.productImageDocId)
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label:"Orders Containing",
              alignment:"RIGHT",
              data:apply(
                #"_a-0000dce6-fa14-8000-60a8-01ef9001ef90_4002"(_),
                tointeger(index(local!gridDatasubset, "identifiers", null))
                ),
              field:"id"
            )
          },
          value: local!gridPagingInfo,
          saveInto: local!gridPagingInfo
        )
      }
    )
  )
)
