rule.definition:
load(
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: if(ri!hideFilters, 5, 10),
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: "eventTimestamp",
        ascending: false
      )
    )
  ),
  local!userFilter,
  local!actionTypeFilter,
  local!startFilter,
  local!endFilter,
  with(
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000daa1-9d78-8000-6ee0-010000010000_1088",
      query: #"SYSTEM_SYSRULES_query"(
        selection: #"SYSTEM_SYSRULES_querySelection"(columns: {
          #"SYSTEM_SYSRULES_queryColumn"(field: "id"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "recordId"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "eventTimestamp"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "initiatorName"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "actionType"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "actionDescription"),
          #"SYSTEM_SYSRULES_queryColumn"(field: "notes"),
        }),
        logicalExpression: if(
          and(
            isnull(ri!recordType),
            isnull(ri!recordId),
            or(isnull(local!userFilter), len(local!userFilter) < 1),
            or(isnull(local!actionTypeFilter), len(local!actionTypeFilter) < 1),
            isnull(local!startFilter),
            isnull(local!endFilter)
          ), 
          null,
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters:{
              if(isnull(ri!recordType), {},
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "recordType",
                  operator: "=",
                  value: #"SYSTEM_SYSRULES_toJson"(ri!recordType)
                )
              ),
              if(isnull(ri!recordId), {}, 
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "recordId",
                  operator: "=",
                  value: ri!recordId
                )
              ),
              if(or(isnull(local!userFilter), len(local!userFilter) < 1), {},
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "initiatorName",
                  operator: "=",
                  value: tostring(local!userFilter)
                )
              ),
              if(or(isnull(local!actionTypeFilter), len(local!actionTypeFilter) < 1), {},
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "actionType",
                  operator: "includes",
                  value: tostring(local!actionTypeFilter)
                )
              ),
              if(isnull(local!startFilter), {},
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "eventTimestamp",
                  operator: ">=",
                  value: local!startFilter
                )
              ),    
              if(isnull(local!endFilter), {},
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "eventTimestamp",
                  operator: "<=",
                  value: local!endFilter
                )
              ),
            }
          )
        ),  
        pagingInfo: local!gridSelection.pagingInfo
      )
    ),
    local!recorddatasubset: if(
      and(isnull(ri!recordId), not(isnull(ri!recordType)), not(isnull(ri!idField)), not(isnull(ri!recordNameField))),
      queryrecord(
        recordType: ri!recordType,
        query: #"SYSTEM_SYSRULES_query"(
          selection: #"SYSTEM_SYSRULES_querySelection"(columns: {
            #"SYSTEM_SYSRULES_queryColumn"(field: ri!idField),
            #"SYSTEM_SYSRULES_queryColumn"(field: ri!recordNameField)
            }
          ),
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: -1,
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: ri!recordNameField,
          ascending: true
          )    
         )
        )
      ),
      {}
    ),
    {
      if(ri!hideFilters,
        {},
        #"SYSTEM_SYSRULES_sectionLayout"(
          label: "Case History Information",
          isCollapsible: true,
          firstColumnContents: {
            #"SYSTEM_SYSRULES_pickerFieldUsers"(
              label: "Initiator Name",
              labelPosition: "ADJACENT",
              maxSelections: 1,
              value: local!userFilter,
              saveInto: {local!userFilter,a!save(local!gridSelection.pagingInfo.startIndex, 1)}
            ),
            #"SYSTEM_SYSRULES_textField"(
              label: "Action Type",
              labelPosition: "ADJACENT",
              value: local!actionTypeFilter,
              saveInto: {local!actionTypeFilter,a!save(local!gridSelection.pagingInfo.startIndex, 1)}
            ),
            #"SYSTEM_SYSRULES_ButtonLayout"(
              secondaryButtons: #"SYSTEM_SYSRULES_ButtonWidget"(
                label: "Clear Filters",
                skipValidation: true,
                saveInto: {
                  a!save(local!userFilter, null),
                  a!save(local!actionTypeFilter, null),
                  a!save(local!startFilter, null),
                  a!save(local!endFilter, null)
                }
              )
            )       
          },
          secondColumnContents: {
            #"SYSTEM_SYSRULES_dateTimeField"(
              label: "Start Time",
              labelPosition: "ADJACENT",
              value: local!startFilter,
              saveInto: {local!startFilter, a!save(local!gridSelection.pagingInfo.startIndex, 1)}
            ),
            #"SYSTEM_SYSRULES_dateTimeField"(
              label: "End Time",
              labelPosition: "ADJACENT",
              validations: if(or(isnull(local!startFilter), isnull(local!endFilter)), "", if(local!endFilter <= local!startFilter, "End date cannot be equal to or before the start date.", "")),
              value: local!endFilter,
              saveInto: {local!endFilter, a!save(local!gridSelection.pagingInfo.startIndex, 1)}
            ),
          }
        )
      ),
      #"SYSTEM_SYSRULES_sectionLayout"(
        label: if(ri!hideFilters, "Case History Information", ""),
        firstColumnContents: {
          #"SYSTEM_SYSRULES_gridField"(
            instructions: if(isnull(ri!recordId), 
              "Audit History information for an individual record can be viewed on the Audit History view, accessible from the main record Summary view.", 
              if(ri!isSelectable,
                "Note: the details of the selected audit history trail will be visible in the News post.",
                ""
              )
            ),
            totalCount: local!datasubset.totalCount,
            columns: {
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Timestamp",
                field: "eventTimestamp",
                data: apply(
                  #"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3920",
                  index(local!datasubset.data, "eventTimestamp", {})
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Initiator Name",
                field: "initiatorName",
                data: apply(#"_a-0000daa1-9d78-8000-6ee0-010000010000_1099", index(local!datasubset.data, "initiatorName", {})),
                links: #"SYSTEM_SYSRULES_applyComponents"(
                  function: #"_a-0000daa1-9d78-8000-6ee0-010000010000_1098"(userName: _), 
                  array: index(local!datasubset.data, "initiatorName", {})
                  )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Action Type",
                field: "actionType",
                data: index(local!datasubset.data, "actionType", {})
              ),
              if(and(isnull(ri!recordId), not(isnull(ri!recordType)), not(isnull(ri!idField)), not(isnull(ri!recordNameField))),
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Record Name",
                  field: "recordName",
                  data: apply(#"_a-0000daa1-9d78-8000-6ee0-010000010000_1389"(
                    rId: _,
                    recordType: ri!recordType,
                    idField: ri!idField,
                    recordNameField: ri!recordNameField,
                    recorddatasubset: local!recorddatasubset
                  ), 
                  index(local!datasubset.data, "recordId", {})),
                  links: #"SYSTEM_SYSRULES_applyComponents"(
                    function: #"SYSTEM_SYSRULES_recordLink"(
                      recordType: ri!recordType,
                      identifier: _
                    ),
                    array: index(local!datasubset.data, "recordId", {})
                    )
                ), {}
              ),               
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Action Description",
                field: "actionDescription",
                data: index(local!datasubset.data, "actionDescription", {})
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Notes",
                field: "notes",
                data: index(local!datasubset.data, "notes", {})
              )

            },
            identifiers: index(local!datasubset.data, "id", {}),

            value: local!gridSelection,
              saveInto: {
                local!gridSelection,
                if(
                  count(local!gridSelection.selected)>1,
                  /* Does not update the value if the user is attempting to select more than one id */
                  {},
                  /* Stores the selected id only */
                  a!save(ri!selectedId, index(index(save!value, "selected", null), 1, null))
                )
              },
            selection: ri!isSelectable,
            validations: if(count(local!gridSelection.selected)>1, "You may only select one audit trail.", null)
          )
        },
        isCollapsible: ri!hideFilters
      )
    }
  )
)
