rule.definition:
load(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 4,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "name",
      ascending: true
    )
  ),
  local!nameSearch,
  local!categorySearch,
  {
    #"SYSTEM_SYSRULES_sectionLayout"(
      label:"What product would you like to add?",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_textField"(
          label: "Search Product by Name",
          value: local!nameSearch,
          saveInto: {
            local!nameSearch,
            a!save(local!pagingInfo.startIndex, 1)
          },
          refreshAfter: "KEYPRESS"
        )
      },
      secondColumnContents: {
        #"SYSTEM_SYSRULES_dropdownField"(
          label: "Search Product by Category",
          placeholderLabel: "--Please select a category.--",
          choiceLabels:#"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3808",
          choiceValues: #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3808",
          value: local!categorySearch,
          saveInto: {
            local!categorySearch,
            a!save(local!pagingInfo.startIndex, 1)
          }
        ),
        #"SYSTEM_SYSRULES_ButtonLayout"(primaryButtons:{
          #"SYSTEM_SYSRULES_ButtonWidget"(
            label:"Cancel",
            style:"DESTRUCTIVE",
            value:true,
            saveInto:{
              a!save(ri!showGrid, true),
              a!save(
                ri!itemsToken,
                if(count(ri!itemsToken)=1, 
                  null, 
                  rdrop(ri!itemsToken, 1)
                )
              )
            }
          )
        })
      }
    ),
    if(
      or(
        not(isnull(local!nameSearch)),
        not(isnull(local!categorySearch))
      ), 
      with(
        local!datasubset: queryrecord(
          #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_3830",
          #"SYSTEM_SYSRULES_query"(
            logicalExpression: {
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  if(
                    isnull(local!nameSearch),
                    null,
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: "name",
                      operator: "includes",
                      value: local!nameSearch
                    )
                  ),
                  if(
                    isnull(local!categorySearch),
                    null,
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: "productCategory",
                      operator: "=",
                      value: local!categorySearch
                    )
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: "isActive",
                    operator: "=",
                    value: true
                  )
                }
              )
            },
            pagingInfo: local!pagingInfo
          )
        ),
        #"SYSTEM_SYSRULES_gridField"(
          instructions:"Click or tap on product name to add it to the Orders grid.",
          totalCount: local!datasubset.totalCount,
          columns: {
            #"SYSTEM_SYSRULES_gridImageColumn"(
              label: "Image",
              field: "productImageDocId",
              data: apply(
                #"SYSTEM_SYSRULES_documentImage"(
                  _,
                  "product image",
                  null,
                  null
                ),
                todocument(index(local!datasubset.data, "productImageDocId", {}))
              ),
              size: "THUMBNAIL"
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Name",
              field: "name",
              data: index(local!datasubset.data, "name", {}),
              links: apply(
                #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_6077"(
                  product: _,
                  OrderItems: ri!OrderItems,
                  itemsToken: ri!itemsToken,
                  nameSearch: local!nameSearch,
                  categoryFilter: local!categorySearch,
                  showGrid: ri!showGrid
                ),
                cast(
                  typeof({'type!{urn:com:appian:types:CASE_APP}CASE_APP_Product'()}),
                  local!datasubset.data
                )
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Product Category",
              field: "productCategory",
              data: index(local!datasubset.data, "productCategory", {})
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Description",
              field: "description",
              data: index(local!datasubset.data, "description", {})
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Price",
              field: "price",
              data: index(local!datasubset.data, "price", {})
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Stock",
              field: "quantity",
              data: index(local!datasubset.data, "quantity", {})
            )
          },
          value: local!pagingInfo,
          saveInto: local!pagingInfo
        )
      ),
      {}
    )
  }
)

a:ruleInputTestConfigs.a:value:
with(
  local!product: #"_a-0001dd13-f0e6-8000-31c3-01ef9001ef90_4720"(10),
  {
    'type!{urn:com:appian:types:CASE_APP}CASE_APP_OrderItem'(
      Quantity: 1,
      OrderItemUnitPrice: local!product.price,
      Product: #"_a-0001dd13-f0e6-8000-31c3-01ef9001ef90_4720"(10)
    )
  }
)

with(
  local!product: #"_a-0001dd13-f0e6-8000-31c3-01ef9001ef90_4720"(10),
  {
    'type!{urn:com:appian:types:CASE_APP}CASE_APP_OrderItem'(
      Quantity: 1,
      OrderItemUnitPrice: local!product.price,
      Product: #"_a-0001dd13-f0e6-8000-31c3-01ef9001ef90_4720"(10)
    )
  }
)
