rule.definition:
/*xpathsnippet(*/
/*  "http://query.yahooapis.com/v1/public/yql?q=select%20*%20from%20yahoo.finance.quotes%20where%20symbol%20in%20(%22" */
/*    & ri!customerData.StockTicker*/
/*    & "%22)&env=store://datatables.org/alltableswithkeys", "/query/results/quote/Name/text()"*/
/*)*/

with(
  local!responseXml: #"SYSTEM_SYSRULES_httpQuery"(
    url: "http://query.yahooapis.com/v1/public/yql",
    method: "GET",
    queryParameters: {
      #"SYSTEM_SYSRULES_httpQueryParameter"(
        "q",
        "select * from yahoo.finance.quotes where symbol in (""" & ri!stockSymbol & """)"
      ),
      #"SYSTEM_SYSRULES_httpQueryParameter"(
        "env",
        "store://datatables.org/alltableswithkeys"
      )
    }
  ).result.body,
  local!companyName: xpathsnippet(local!responseXml, "/query/results/quote/Name/text()"),
  if(
    #"SYSTEM_SYSRULES_isNullOrEmpty"(local!companyName),
    null,
    local!companyName[1]
  )
)
