rule.definition:
/*  A default country value must be provided for this form to work properly. 
Input a value such as () to load the form. 
Country names must match the list in cons!CASE_APP)_CountriesList
*/
= #"SYSTEM_SYSRULES_sectionLayout"(
  label: ri!sectionTitle,
  isCollapsible: ri!isCollapsible,
  firstColumnContents: {
    if(
      ri!readOnly,
      {
        #"SYSTEM_SYSRULES_textField"(
          label: "Country",
          labelPosition: ri!labelPosition,
          value: ri!addressData.country,
          readOnly: true()
        )
      },
      {
        #"SYSTEM_SYSRULES_dropdownField"(
          /*Dropdown for selecting a country */
          label: "Country",
          instructions: if(
            ri!readOnly,
            null,
            "Select the country"
          ),
          labelPosition: ri!labelPosition,
          required: true,
          disabled: ri!readOnly,
          choiceLabels: #"_a-0000d9b5-6c6c-8000-9e06-010000010000_756",
          choiceValues: #"_a-0000d9b5-6c6c-8000-9e06-010000010000_756",
          placeholderLabel: "--Select a country--",
          value: ri!addressData.country,
          saveInto: {
            ri!addressData.country,
            a!save(
              ri!addressData.state,
              null
            )
          }
        )
      }
    ),
    #"SYSTEM_SYSRULES_paragraphField"(
      /*Text field capture for Address - paragraph is used to capture multi line Addresses */
      label: "Address",
      instructions: if(
        ri!readOnly,
        null,
        len(ri!addressData.Address) & " of 100"
      ),
      height: "SHORT",
      labelPosition: ri!labelPosition,
      readOnly: ri!readOnly,
      value: ri!addressData.Address,
      saveInto: ri!addressData.Address,
      refreshAfter: "KEYPRESS",
      validations: {
        if(
          len(ri!addressData.Address) > 100,
          "You may not exceed 100 characters",
          null
        )
      }
    )
  },
  secondColumnContents: {
    #"SYSTEM_SYSRULES_textField"(
      /*Simple City capture text field */
      label: "City",
      instructions: if(
        ri!readOnly,
        null,
        len(ri!addressData.City) & " of 100"
      ),
      labelPosition: ri!labelPosition,
      readOnly: ri!readOnly,
      value: ri!addressData.City,
      saveInto: ri!addressData.City,
      refreshAfter: "KEYPRESS",
      validations: {
        if(
          len(ri!addressData.City) > 100,
          "You may not exceed 100 characters",
          null
        )
      }
    ),
    /* A limited number of countries require a State or Province to be specified in the address area.
     * The dropdown for State/Province selection only displays if the country name is in the list of
     * countries that requires that value.
     * Refer to the cons!CASE_APP_CountrieswithStates for a list of countries that requires this 
     */
    if(
      #"_a-0000d9b9-57db-8000-9e06-010000010000_808"(
        if(
          isnull(ri!addressData.country),
          #"_a-0000d9b9-6309-8000-9ba2-011c48011c48_2093",
          ri!addressData.country
        )
      ),
      {
        if(
          ri!readOnly,
          {
            #"SYSTEM_SYSRULES_textField"(
              label: "State/Province",
              labelPosition: ri!labelPosition,
              value: ri!addressData.state,
              readOnly: true()
            )
          },
          {
            #"SYSTEM_SYSRULES_dropdownField"(
              label: "State/Province",
              labelPosition: ri!labelPosition,
              disabled: ri!readOnly,
              required: true,
              choiceLabels: #"_a-0000d9b9-57db-8000-9e06-010000010000_816"(
                if(
                  isnull(
                    ri!addressData.country
                  ),
                  #"_a-0000d9b9-6309-8000-9ba2-011c48011c48_2093",
                  ri!addressData.country
                )
              ),
              /* The rule simply checks if the country selected exists in cons!CASE_APP_CountrieswithStates */
              choiceValues: #"_a-0000d9b9-57db-8000-9e06-010000010000_816"(
                if(
                  isnull(
                    ri!addressData.country
                  ),
                  #"_a-0000d9b9-6309-8000-9ba2-011c48011c48_2093",
                  ri!addressData.country
                )
              ),
              placeholderLabel: "-- Select a State/Province --",
              value: ri!addressData.state,
              saveInto: ri!addressData.state
            )
          }
        )
      },
      {}
    ),
    #"SYSTEM_SYSRULES_textField"(
      label: "Postal Code",
      instructions: if(
        ri!readOnly,
        null,
        len(ri!addressData.ZIP) & " of 50"
      ),
      labelPosition: ri!labelPosition,
      readOnly: ri!readOnly,
      value: ri!addressData.ZIP,
      saveInto: ri!addressData.ZIP,
      refreshAfter: "KEYPRESS",
      validations: {
        if(
          len(ri!addressData.ZIP) > 50,
          "You may not exceed 50 characters",
          null
        )
      }
    ),
    
  }
)
