rule.definition:
=#"SYSTEM_SYSRULES_gridRowLayout"(
  contents: {
    #"SYSTEM_SYSRULES_textField"(
      label: "name " & ri!index,
      value: ri!items[ri!index].name,
      saveInto: ri!items[ri!index].name,
      required: true
    ),
    #"SYSTEM_SYSRULES_textField"(
      label: "description " & ri!index,
      value: ri!items[ri!index].description,
      saveInto: ri!items[ri!index].description,
      required: false
    ),
    #"SYSTEM_SYSRULES_dateField"(
      label:"dueDate "& ri!index,
      value: ri!items[ri!index].dueDate,
      saveInto: ri!items[ri!index].dueDate,
      required: true,
      validations:if(ri!items[ri!index].dueDate<today(),"You cannot enter dates in the past.",null)
    ),
    #"SYSTEM_SYSRULES_imageField"(
      label:"MoveUp"&ri!index,
      images:#"SYSTEM_SYSRULES_documentImage"(
        document:#"SYSTEM_SYSRULES_iconIndicator"(
          if(ri!index=1,
            "MOVE_UP_DISABLED",
            "MOVE_UP"
          )
        ),
        altText:"Move Up",
        caption:"Move Up",
        link:if(ri!index=1,
          {},
          #"SYSTEM_SYSRULES_dynamicLink"(
            value:cast(1,ri!items[ri!index].order)-1,
            saveInto:{
              a!save(target:ri!items[ri!index-1].order, value:save!value+1),
              a!save(target:ri!items[ri!index].order, value:save!value),
              a!save(
                target:ri!items,
                value: todatasubset(ri!items,#"SYSTEM_SYSRULES_pagingInfo"(startIndex:1,batchSize:-1, sort:#"SYSTEM_SYSRULES_sortInfo"(field:"order",ascending:true))).data
              )
            }
          )
        )
      ),
      size:"ICON"
    ),
    #"SYSTEM_SYSRULES_imageField"(
      label:"MoveDown"&ri!index,
      images:#"SYSTEM_SYSRULES_documentImage"(
        document:#"SYSTEM_SYSRULES_iconIndicator"(
          if(or(count(ri!items)=1,count(ri!items)=ri!index),
            "MOVE_DOWN_DISABLED",
            "MOVE_DOWN"
          )
        ),
        altText:"Move Down",
        caption:"Move Down",
        link: if(or(count(ri!items)=1,count(ri!items)=ri!index),
          {},
          #"SYSTEM_SYSRULES_dynamicLink"(
            value:cast(1,ri!items[ri!index].order)+1,
            saveInto:{
              a!save(target:ri!items[ri!index+1].order, value:save!value-1),
              a!save(target:ri!items[ri!index].order, value:save!value),
              a!save(
                target:ri!items,
                value: todatasubset(ri!items,#"SYSTEM_SYSRULES_pagingInfo"(startIndex:1,batchSize:-1, sort:#"SYSTEM_SYSRULES_sortInfo"(field:"order",ascending:true))).data
              )
            }
          )
        )
      ),
      size:"ICON"
    ),
    #"SYSTEM_SYSRULES_linkField"(
      label: "delete " & ri!index,
      links: #"SYSTEM_SYSRULES_dynamicLink"(
        label: char(10005),
        value: ri!index,
        saveInto: {
          ri!items << fn!remove(ri!items, _),
          ri!itemsToken << fn!remove(ri!itemsToken, _)
        }
      ),
      align: "CENTER"
    )
  },
  id: ri!index
)
