rule.definition:
=#"SYSTEM_SYSRULES_gridRowLayout"(
  contents: {
    #"SYSTEM_SYSRULES_textField"(
      /* Labels in the row contents are for debugging purposes only */
      label: "name " & ri!index,
      value: ri!items[ri!index].name,
      saveInto: ri!items[ri!index].name,
      required: true
    ),
    #"SYSTEM_SYSRULES_checkboxField"(
      label: "isActive " & ri!index,
      choiceLabels:{""},
      placeholderLabel: "Select an option",
      choiceValues:{true},
      value: ri!items[ri!index].isActive,
      saveInto: ri!items[ri!index].isActive,
      required:true
    ),
    #"SYSTEM_SYSRULES_linkField"(
      label:"Setup Milestones " & ri!index,
      links: #"SYSTEM_SYSRULES_dynamicLink"(
        label:"Setup",
        value: ri!items[ri!index],
        saveInto: {
          a!save(target: ri!selectedCategory, value: cast(typeof('type!{urn:com:appian:types:CASE_APP}CASE_APP_ICM_Categories'()), save!value)),
          a!save(target: ri!selectedCategoryIdx, value: ri!index)
        }
      )
    ),
    #"SYSTEM_SYSRULES_imageField"(
      label:"MoveUp"&ri!index,
      images:#"SYSTEM_SYSRULES_documentImage"(
        document:#"SYSTEM_SYSRULES_iconIndicator"(
          if(ri!index=1,
            "MOVE_UP_DISABLED",
            "MOVE_UP"
          )
        ),
        altText:"Move Up",
        caption:"Move Up",
        link:if(ri!index=1,
          {},
          #"SYSTEM_SYSRULES_dynamicLink"(
            value:cast(1,ri!items[ri!index].order)-1,
            saveInto:{
              a!save(target:ri!items[ri!index-1].order, value:save!value+1),
              a!save(target:ri!items[ri!index].order, value:save!value),
              a!save(
                target:ri!items,
                value: todatasubset(ri!items,#"SYSTEM_SYSRULES_pagingInfo"(startIndex:1,batchSize:-1, sort:#"SYSTEM_SYSRULES_sortInfo"(field:"order",ascending:true))).data
              )
            }
          )
        )
      ),
      size:"ICON"
    ),
    #"SYSTEM_SYSRULES_imageField"(
      label:"MoveDown"&ri!index,
      images:#"SYSTEM_SYSRULES_documentImage"(
        document:#"SYSTEM_SYSRULES_iconIndicator"(
          if(or(count(ri!items)=1,count(ri!items)=ri!index),
            "MOVE_DOWN_DISABLED",
            "MOVE_DOWN"
          )
        ),
        altText:"Move Down",
        caption:"Move Down",
        link: if(or(count(ri!items)=1,count(ri!items)=ri!index),
          {},
          #"SYSTEM_SYSRULES_dynamicLink"(
            value:cast(1,ri!items[ri!index].order)+1,
            saveInto:{
              a!save(target:ri!items[ri!index+1].order, value:save!value-1),
              a!save(target:ri!items[ri!index].order, value:save!value),
              a!save(
                target:ri!items,
                value: todatasubset(ri!items,#"SYSTEM_SYSRULES_pagingInfo"(startIndex:1,batchSize:-1, sort:#"SYSTEM_SYSRULES_sortInfo"(field:"order",ascending:true))).data
              )
            }
          )
        )
      ),
      size:"ICON"
    ),
    #"SYSTEM_SYSRULES_linkField"(
      label: "delete " & ri!index,
      links: #"SYSTEM_SYSRULES_dynamicLink"(
        label: char(10005),
        value: ri!index,
        saveInto: {
          a!save(ri!deletedCategoryIds, append(ri!deletedCategoryIds, index(ri!items.id, save!value, null))),
          a!save(ri!items, fn!remove(ri!items, save!value)),
          a!save(ri!itemsToken, fn!remove(ri!itemsToken, save!value))
        }
      ),
      align: "CENTER"
    )
  },
  id: ri!index
)
