rule.definition:
= #"SYSTEM_SYSRULES_documentImage"(
  document: if(
    #"SYSTEM_SYSRULES_isNullOrEmpty"(ri!doc),
    #"_a-0000dc71-b531-8000-5126-01ef9001ef90_4603",
    with(
      local!ext: upper(document(ri!doc,"extension")),
      local!isImage: contains(#"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3516", local!ext),
      local!isDoc: contains(#"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3515", local!ext),
      if(
        and(
          not(local!isImage),
          not(local!isDoc)
        ),
        #"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3518"[1],
        if(
          local!isImage,
          ri!doc,
          #"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3518"[wherecontains(
            local!ext, #"_a-0000da89-b5e9-8000-9ba2-011c48011c48_3519"
          )]
        )
      )
    )
  ),
  altText: document(ri!doc,"extension"),
  caption: document(ri!doc,"extension")
)
