rule.definition:
#"SYSTEM_SYSRULES_barcodeField"(
  label: ri!label,
  value: ri!skuSearch,
  acceptedTypes: "CODE39",
  saveInto: {
    ri!skuSearch,
    if(
      or(
        len(ri!skuSearch) = 7, 
        len(ri!skuSearch) = 12 
      ),
      {
        with(
          local!product: cast(
            'type!{urn:com:appian:types:CASE_APP}CASE_APP_Product',
            #"SYSTEM_SYSRULES_queryEntity"(
              #"_a-0000d9c4-bcac-8000-9ba2-011c48011c48_4329",
              #"SYSTEM_SYSRULES_query"(
                logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
                  operator: "AND",
                  filters: {
                    if(
                      isnull(ri!skuSearch),
                      null,
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: "sku",
                        operator: "includes",
                        value: ri!skuSearch
                      )
                    ),
                    #"SYSTEM_SYSRULES_queryFilter"(
                      field: "isActive",
                      operator: "=",
                      value: true
                    )
                  }
                ),
                pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1,1,null)
            )
          ).data
          ),
          local!orderItem: if(
            #"SYSTEM_SYSRULES_isNullOrEmpty"(local!product),
            null,
            'type!{urn:com:appian:types:CASE_APP}CASE_APP_OrderItem'(
              OrderItemUnitPrice: local!product.price,
              Product: local!product,
              Quantity: 1,
              Taxed: true()
            )
          ),
          local!idx: if(
            or(
              #"SYSTEM_SYSRULES_isNullOrEmpty"(ri!OrderItems),
              #"SYSTEM_SYSRULES_isNullOrEmpty"(ri!OrderItems.Product),
              #"SYSTEM_SYSRULES_isNullOrEmpty"(local!product)
            ),
            null,
            wherecontains(local!product.id, ri!OrderItems.Product.id)
          ),
          {
            if(
              isnull(local!product.id),
              a!save(ri!skuSearch, null),
              {
                if(
                  #"SYSTEM_SYSRULES_isNullOrEmpty"(local!idx),
                  {
                    a!save(
                      ri!OrderItems,
                      append(ri!OrderItems, local!orderItem)
                    ),
                    a!save(
                      ri!itemsToken,
                      append(ri!itemsToken, local!orderItem)
                    )
                  },
                  a!save(
                    target: ri!OrderItems[local!idx].Quantity,
                    value: tointeger(ri!OrderItems[local!idx].Quantity) + 1
                  )
                ),
                a!save(ri!skuSearch, null)
              }
            )
          }
        )
      },
      {}
    )
  },
  refreshAfter: "KEYPRESS"
)
