rule.definition:
load(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: -1,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "order",
      ascending: true
    )
  ),
  local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
    entity: #"_a-0000d9b9-6309-8000-9ba2-011c48011c48_2297",
    query: #"SYSTEM_SYSRULES_query"(
      pagingInfo: local!pagingInfo
    )
  ),
  local!itemsToken,
  #"SYSTEM_SYSRULES_formLayout"(
    label:"Manage Category Milestones",
    instructions: "Select a category and define the milestones to associate with it.",
    firstColumnContents:{
      if(local!datasubset.totalCount>0,
        #"SYSTEM_SYSRULES_dropdownField"(
          label:"Select an Investigation Category",
          choiceLabels:local!datasubset.data.name,
          placeholderLabel:"--Select Category--",
          choiceValues:local!datasubset.data.id,
          value:ri!category.id,
          saveInto: a!save(
            target:ri!category,
            value:cast(
              typeof('type!{urn:com:appian:types:CASE_APP}CASE_APP_ICM_Categories'()),
              local!datasubset.data[wherecontains(save!value,local!datasubset.data.id)]
            )
          )
        ),
        #"SYSTEM_SYSRULES_richTextDisplayField"(value:#"SYSTEM_SYSRULES_richTextItem"(
          text:"There are no categories. Please create at least one category first using the 'Update Investigation Categories' Action",
          style:"HEADER_SMALL"
          ))
      ),
      if(#"SYSTEM_SYSRULES_isNullOrEmpty"(ri!category),
        {},
        #"SYSTEM_SYSRULES_sectionLayout"(
          label:"Associated Milestones for Investigation Category",
          firstColumnContents:{
            #"SYSTEM_SYSRULES_gridLayout"(
              headerCells: {
                #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Milestone Name"),
                #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Description"),
                #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Active?"),
                #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: ""),
                #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: ""),
                #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "")
              },
              columnConfigs: {
                #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
                #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
                #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
                #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
                #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
                #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW")
              },
              rows: #"SYSTEM_SYSRULES_applyComponents"(
                function: #"3684e015-83a8-42bc-926c-aa5725e72322"(
                  items: ri!category.presetMilestones,
                  index: _,
                  itemsToken: local!itemsToken
                ),
                array: if(or(#"SYSTEM_SYSRULES_isNullOrEmpty"(ri!category.presetMilestones), count(ri!category.presetMilestones) < 1), {}, 1+enumerate(count(ri!category.presetMilestones))),
                arrayVariable: local!itemsToken
              ),
              validations:if(count(ri!category.presetMilestones)<2,"You must enter at least 2 Milestones/Objectives",null)
            ),
            #"SYSTEM_SYSRULES_linkField"(
              label: "Add Link",
              labelPosition: "COLLAPSED",
              links: #"SYSTEM_SYSRULES_dynamicLink"(
                label: "+Add Milestone",
                value: 'type!{urn:com:appian:types:CASE_APP}CASE_APP_ICM_Milestones'(order:count(ri!category.presetMilestones)+1, isActive:true),
                saveInto: {
                  ri!category.presetMilestones << fn!append(ri!category.presetMilestones, _),
                  local!itemsToken << fn!append(local!itemsToken, _)
                }
              )
            )
          }
        )
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons:{
        #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
          label:"Submit",
          style:"PRIMARY",
          saveInto:{
            a!save(target:ri!isCancel,value:false)
          },
          disabled:local!datasubset.totalCount=0
        )
      },
      secondaryButtons:{
        #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
          label:"Cancel",
          value:true,
          saveInto:ri!isCancel,
          skipValidation:true
        )
      }
    )
  )
)
