rule.definition:
{
  #"769503c1-a459-4f96-82e6-7120e9c69fb4"(
    sectionTitle: "Customer Shipping Address",
    addressData: ri!customerData.ShippingAddress,
    readOnly: false,
    labelPosition: null
  ),
  #"SYSTEM_SYSRULES_checkboxField"(
    labelPosition: "COLLAPSED",
    choiceLabels: {
      "Are shipping and billing addresses the same?"
    },
    choiceValues: {
      true
    },
    value: if(
      ri!isShippingBillingSame=true,
      ri!isShippingBillingSame=true,
      null
    ),
    saveInto: {
      a!save(
        ri!isShippingBillingSame,
        save!value
      ),
      a!save(
        ri!customerData.BillingAddress.Address,
        ri!customerData.ShippingAddress.Address
      ),
      a!save(
        ri!customerData.BillingAddress.Country,
        ri!customerData.ShippingAddress.Country
      ),
      a!save(
        ri!customerData.BillingAddress.City,
        ri!customerData.ShippingAddress.City
      ),
      a!save(
        ri!customerData.BillingAddress.State,
        ri!customerData.ShippingAddress.State
      ),
      a!save(
        ri!customerData.BillingAddress.ZIP,
        ri!customerData.ShippingAddress.ZIP
      )
    }
  ),
  /*If the shipping and billing address are the same, 
    a subsequent script task will overwrite billing address with shipping address 
    in order to render the billing address dynamically after the user selects the checkbox*/
  #"769503c1-a459-4f96-82e6-7120e9c69fb4"(
    sectionTitle: "Customer Billing Address",
    readOnly: ri!isShippingBillingSame,
    labelPosition: null,
    addressData: if(
      ri!isShippingBillingSame,
      ri!customerData.ShippingAddress,
      ri!customerData.BillingAddress
    )
  )
}
