rule.definition:
=load(
  local!itemsToken,
  local!selectedItems,
  local!showGrid: true,
  local!skuSearch,
  {
    if(
      local!showGrid,
      {
        if(
          ri!readOnly = false(),
          #"_a-0001dd13-f0e6-8000-31c3-01ef9001ef90_4730"(
            label: "Scan Product",
            skuSearch: local!skuSearch,
            OrderItems: ri!OrderItems,
            itemsToken: local!itemsToken
          ),
          {}
        ),
        #"SYSTEM_SYSRULES_gridLayout"(
          label: ri!gridLabel,
          instructions: ri!gridInstructions,
          headerCells:{
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Image"),
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Product Details"),
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Unit Price"),
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Quantity"),
            #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "")
          },
          columnConfigs:{
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 1),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(weight: 4),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON")
          },
          rows: #"SYSTEM_SYSRULES_applyComponents"(
            function: #"1ffac6f0-8bc9-46ee-b624-5a615fbaf482"(
              OrderItems: ri!OrderItems,
              index: _,
              itemsToken: local!itemsToken,
              readOnly: ri!readOnly
            ),
            array: if(or(#"_a-0000d9b9-6309-8000-9ba2-011c48011c48_1982"(ri!OrderItems), count(ri!OrderItems) < 1), {}, 1+enumerate(count(ri!OrderItems))),
            arrayVariable: local!itemsToken
          ),
          addRowLink: if( 
            ri!readOnly,
            null,
            #"SYSTEM_SYSRULES_dynamicLink"(
              label: "Manually Add Item",
              value: false,
              saveInto: local!showGrid
            )
          ),
          validations: {
            if(
              or(#"_a-0000d9b9-6309-8000-9ba2-011c48011c48_1982"(ri!OrderItems), count(ri!OrderItems) < 1),
              "You must add at least 1 item to the order",
              if(
                contains(ri!OrderItems.Product.name, null),
                "Select a product below to add to the order",
                null
              )
            )
          }
        )
      },
      {}
    ),
    if(ri!readOnly, 
      {}, 
      if(
        local!showGrid,
        {},
        #"3c080665-5d73-4dcc-b04a-2bec25d26b79"(
          OrderItems: ri!OrderItems,
          itemsToken: local!itemsToken,
          showGrid: local!showGrid
        )
      )
    )
  }                
)
